function Login_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'login_username': 'Login_login_username',
        'login_password': 'Login_login_password',
        'mobilegroupedbuttons_23': 'Login_mobilegroupedbuttons_23',
        'login_button': 'Login_login_button',
        'register_button': 'Login_register_button'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["Login_user_login_onbeforesend_mapping_0"] = {
        "homeScreen": "Login",
        "directions": [

        {
            "from_name": "Login",
            "from_type": "UI",

            "to_name": "user_login",
            "to_type": "SERVICE_REQUEST",

            "to_default": {
                "headers": {
                    "X-Appery-Database-Id": "{database_id}"
                },
                "parameters": {},
                "body": null
            },

            "mappings": [

            {

                "source": "$['login_username:text']",
                "target": "$['parameters']['username']"

            },

            {

                "source": "$['login_password:text']",
                "target": "$['parameters']['password']"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["Login_user_login_onsuccess_mapping_0"] = {
        "homeScreen": "Login",
        "directions": [

        {
            "from_name": "user_login",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "user_session_token",
            "to_type": "LOCAL_STORAGE",

            "mappings": [

            {

                "source": "$['body']['sessionToken']",
                "target": "$"

            }

            ]
        },

        {
            "from_name": "Login",
            "from_type": "UI",

            "to_name": "username",
            "to_type": "LOCAL_STORAGE",

            "mappings": [

            {

                "source": "$['login_username:text']",
                "target": "$"

            }

            ]
        },

        {
            "from_name": "user_login",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "userID",
            "to_type": "LOCAL_STORAGE",

            "mappings": [

            {

                "source": "$['body']['_id']",
                "target": "$"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["Login_user_register_onsuccess_mapping_0"] = {
        "homeScreen": "Login",
        "directions": [

        {
            "from_name": "user_register",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "user_session_token",
            "to_type": "LOCAL_STORAGE",

            "mappings": [

            {

                "source": "$['body']['sessionToken']",
                "target": "$"

            }

            ]
        },

        {
            "from_name": "user_register",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "username",
            "to_type": "LOCAL_STORAGE",

            "mappings": [

            {

                "source": "$['body']['username']",
                "target": "$"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["Login_user_register_onbeforesend_mapping_0"] = {
        "homeScreen": "Login",
        "directions": [

        {
            "from_name": "Login",
            "from_type": "UI",

            "to_name": "user_register",
            "to_type": "SERVICE_REQUEST",

            "to_default": {
                "headers": {
                    "X-Appery-Database-Id": "{database_id}",
                    "Content-Type": "application/json"
                },
                "parameters": {},
                "body": null
            },

            "mappings": [

            {

                "source": "$['login_username:text']",
                "target": "$['body']['username']"

            },

            {

                "source": "$['login_password:text']",
                "target": "$['body']['password']"

            }

            ]
        }

        ]
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.user_login = Apperyio.datasources.user_login = new Apperyio.DataSource(WarehouseDB_login_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["Login_user_login_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

            Apperyio.refreshScreenFormElements("Login");
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["Login_user_login_onsuccess_mapping_0"]);
            Apperyio.navigateTo('Welcome', {
                reverse: false
            });
        },
        "onError": function(jqXHR, textStatus, errorThrown) {
            alert("Login Failed");
        }
    });

    window.user_register = Apperyio.datasources.user_register = new Apperyio.DataSource(WarehouseDB_signup_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["Login_user_register_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

            Apperyio.refreshScreenFormElements("Login");
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["Login_user_register_onsuccess_mapping_0"]);
            Apperyio.navigateTo('Welcome', {
                reverse: false
            });
        },
        "onError": function(jqXHR, textStatus, errorThrown) {
            alert("Signup Failed");
        }
    });

    Apperyio.CurrentScreen = 'Login';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var Login_onLoad = function() {
            Login_elementsExtraJS();

            Login_deviceEvents();
            Login_windowEvents();
            Login_elementsEvents();
        };

    // screen window events


    function Login_windowEvents() {

        $('#Login').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function Login_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function Login_elementsExtraJS() {
        // screen (Login) extra code

    };

    // screen elements handler


    function Login_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#Login_mobilecontainer [name="login_button"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    var email = Apperyio("login_username").val();
                    if (email === "") {
                        $alert("Please enter a valid email address");
                        return;
                    }
                    var reg = /^([A-Za-z0-9-.])+@([A-Za-z0-9-.])+.([A-Za-z]{2,4})$/;
                    if (reg.test(email) === false) {
                        alert('please enter a valid email address');
                        return;
                    };
                    try {
                        user_login.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };

                }
            },
        }, '#Login_mobilecontainer [name="login_button"]');
        $(document).off("click", '#Login_mobilecontainer [name="register_button"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    var email = Apperyio("login_username").val();
                    if (email === "") {
                        $alert("Please enter a valid email address");
                        return;
                    }
                    var reg = /^([A-Za-z0-9-.])+@([A-Za-z0-9-.])+.([A-Za-z]{2,4})$/;
                    if (reg.test(email) === false) {
                        alert('please enter a valid email address');
                        return;
                    };
                    try {
                        user_register.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };

                }
            },
        }, '#Login_mobilecontainer [name="register_button"]');

    };

    $(document).off("pagebeforeshow", "#Login").on("pagebeforeshow", "#Login", function(event, ui) {
        Apperyio.CurrentScreen = "Login";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    Login_onLoad();
};

$(document).off("pagecreate", "#Login").on("pagecreate", "#Login", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Login_js();
});